INSERT INTO `goods_table` VALUES ('50', '1000', '4GB+32GB内存版 玫瑰金 全网通4G手机 双卡双待', '玫瑰金', '1999', '60', 'OPPO A59s', null, '<p><a href=\"https://sale.jd.com/act/IeGA2F5vSpBjaWxV.html?cpdad=1DLSUE\" target=\"_blank\"><img alt=\"\" class=\"\" src=\"https://img10.360buyimg.com/cms/jfs/t4225/59/788301869/68784/8bf2f7a3/58b8e355N620a1f5a.jpg\" width=\"750\" height=\"150\"/></a></p><p><a href=\"https://mall.jd.com/view_page-44993259.html\" target=\"_blank\"><img alt=\"\" class=\"\" src=\"https://img30.360buyimg.com/jgsq-productsoa/jfs/t3865/261/1346601502/42766/62679f26/5874751dN28a1cf87.jpg\"/></a><br/></p><table width=\"100%\"><tbody><tr class=\"firstRow\"><td><img class=\"\" src=\"https://img14.360buyimg.com/cms/jfs/t3916/137/1205870327/142758/4b27d9c1/586e1554N6e7d4526.jpg\" width=\"750\" height=\"836\"/></td></tr></tbody></table><p><img alt=\"\" class=\"\" src=\"https://img10.360buyimg.com/cms/jfs/t3715/90/427006992/69876/ba1b54c4/58096ed1N5fc1078f.jpg\"/> \r\n </p><p><img alt=\"\" class=\"\" src=\"https://img30.360buyimg.com/cms/jfs/t3598/247/421256841/110084/c237b97c/58096ed1N0497eb47.jpg\"/> \r\n &nbsp;</p><p style=\"line-height: 1.5em; text-align: center;\"><span style=\"font-family: 微软雅黑, \">美好的时刻就应尽情自拍。A59s搭载1600万像素前置摄像头，拥有更强的感光能力，即使弱光下也能拍出明亮动人的照片。搭配全新升级美颜4.0，自拍真实自然又更美一点，每一张都值得珍藏。</span> </p><p><img alt=\"\" class=\"\" src=\"https://img12.360buyimg.com/cms/jfs/t3343/109/402273738/86531/3b524811/58096ed1Ne2e1c68b.jpg\"/> \r\n &nbsp;</p><p style=\"text-align: center; line-height: 1.5em;\"><span style=\"font-family: 微软雅黑, \">A59s采用金属机身，质感出众。机身设计简洁大气，手感出众。金属后盖搭配精细锆砂，两条纤细生辉的流光边游走其上，仿佛是光的天然雕刻，细腻而灵动，令你一见倾心，百看不厌。</span> </p><p><img alt=\"\" class=\"\" src=\"https://img10.360buyimg.com/cms/jfs/t3454/347/420159706/173732/5e4098f7/58096ed2N2b86b021.jpg\"/> \r\n &nbsp;</p><p style=\"line-height: 1.5em; text-align: center;\"><span style=\"font-family: 微软雅黑, \">采用4GB超大运行内存，可同时运行更多APP，运行速度更快，手机不容易卡顿。32GB存储内存，支持128GB内存扩展，可将一整部电视剧装进手机，出门在外随时娱乐。</span> </p><p><img alt=\"\" class=\"